css:
.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  font-size: 1.5em;
  line-height: 1.4;
  padding: 4em;
  color: #a2a5ae;
  background-color: #1c1d21;
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .icon::before {
  margin-right: 0;
  font-size: 1.5em;
  width: auto;
}
.about .about-container {
  width: 100%;
  max-width: 500px;
}
.about .about-header {
  text-align: center;
}
.about .about-header .about-atom-io:hover .about-logo {
  color: #40a977;
}
.about .about-header .about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 0.4em auto;
  color: #d9dade;
  transition: color 0.2s;
}
.about .about-header .about-heading {
  font-size: 1.5em;
  font-weight: bold;
}
.about .about-header .about-version-container:hover {
  color: #cbcdd2;
}
.about .about-header .about-version-container:active {
  color: #f4f4f6;
}
.about .about-header .about-version-container .about-version {
  margin-right: 8px;
}
.about .about-header .about-version-container .about-copy-version::before {
  position: relative;
  bottom: 1px;
}
.about .about-header button {
  font-size: 1em;
}
.about .about-header .icon::before {
  font-size: 1em;
}
.about .about-actions {
  margin-top: 3em;
  font-size: .86em;
  text-align: center;
}
.about .about-note {
  font-size: .86em;
  color: rgba(162, 165, 174, 0.6);
}
.about .about-metrics {
  margin-top: 2em;
}
.about .about-credits {
  margin-top: 40px;
  text-align: center;
}
.about .about-love {
  margin-top: 40px;
  text-align: center;
  font-size: .86em;
}
.about .about-love .icon::before {
  position: relative;
  top: 1px;
}
.about .about-love .icon-logo-github::before {
  top: 3px;
}
.about-release-notes {
  color: #0088ff;
}
